import React from "react";
import {Link} from 'react-router-dom'
import {useSelector} from 'react-redux'
import axios from 'axios'

function Header(){
    const auth = useSelector(state=>state.auth)

    const {user, isLogged}=auth
    
    const userLink = () => {
        return <li className="drop-nav">
            <Link to="#" className="avatar">
            <img src={user.avatar} alt=""/> {user.name} <i className="fas fa-angle-down"></i>
            </Link>
            <ul className="dropdown">
                <li><Link to="/profile">Profile</Link></li>
                <li><Link to="/" onClick={handleLogout}>Logout</Link></li>

                {/* <li><Link to="/" onClick={handleLogout}>Logout</Link></li> */}
            </ul>
        </li>
    }
    return(
        <header>
        <div className="logo">
            <h1><Link to="/">Mọt Sách</Link></h1>
        </div>

        <ul>
            <li><Link to="/"><i className=""></i> Homepage</Link></li>
            <li><Link to="/introduction"><i className=""></i> Introduction</Link></li>
            <li><Link to="/FAQ"><i className=""></i> FAQ</Link></li>
            {
                isLogged
                ? userLink()
                :<li><Link to="/login"><i className=""></i>Log in</Link></li>
            }
            {
                isLogged
                ? <li></li>
                :<li><Link to="/register"><i className=""></i>Register</Link></li>
            }
        </ul>        
    </header>
    )
}

export default Header
