import React from "react";
import {Link} from 'react-router-dom'
import {useSelector} from 'react-redux'
import axios from 'axios'

function Header(){
    const auth = useSelector(state=>state.auth)

    const {user, isLogged}=auth
    
    const userLink=()=>{
        return <li>
            <Link to="/"><i className=""></i> Homepage</Link>
            {/* <link>
            <img src={user.avatar} alt=""/>{user.name}
            </link> */}
        </li>
    }
    return(
        <header>
        <div className="logo">
            <h1><Link to="/">Mọt Sách</Link></h1>
        </div>

        <ul>
            <li><Link to="/"><i className=""></i> Homepage</Link></li>
            <li><Link to="/introduction"><i className=""></i> Introduction</Link></li>
            <li><Link to="/FAQ"><i className=""></i> FAQ</Link></li>
            {
                isLogged
                ? userLink()
                :<li><Link to="/login"><i className=""></i>Log in</Link></li>
            }
            {
                isLogged
                ? <li></li>
                :<li><Link to="/register"><i className=""></i>Register</Link></li>
            }
        </ul>        
    </header>
    )
}

export default Header
